/**
 * WYSIWYG Editor ( For editing text module )
 *
 * - dslc_show_wysiwyg ( show the editor )
 * - dslc_hide_wysiwyg ( hide the editor )
 * - dslc_save_wysiwyg ( save the changes made in the editor )
 */

	function dslc_show_wysiwyg( module ) {

		jQuery('.dslca-module-wysiwyg-active').removeClass('dslca-module-wysiwyg-active');
		jQuery('.dslca-module-being-edited').removeClass('dslca-module-being-edited');
		module.addClass('dslca-module-wysiwyg-active dslca-module-being-edited');

		var moduleHeight = module.outerHeight();
		var moduleWidth = module.outerWidth();
		var moduleOffset = module.offset();
		var moduleContentInput = module.find('.dslca-module-options-front textarea[data-id="content"]');

		jQuery('#dslcawysiwyg').val( moduleContentInput.val() );

		jQuery('#dslca-wysiwyg').css({
			/* height : moduleHeight, */
			width : moduleWidth,
			left : moduleOffset.left,
			top : moduleOffset.top
		})

	}

	function dslc_hide_wysiwyg() {

		jQuery('#dslca-wysiwyg').css({
			left : '-110%',
			top : 0
		});

	}

	function dslc_save_wysiwyg() {

		var module = jQuery('.dslca-module-wysiwyg-active');
		var moduleContent = module.find('.dslc-content');
		var moduleContentInput = module.find('.dslca-module-options-front textarea[data-id="content"]');
		jQuery('#dslcawysiwyg-html').click();
		moduleContentInput.val( jQuery('#dslcawysiwyg').val() );
		moduleContent.html( jQuery('#dslcawysiwyg').val() );
		dslc_preview_change();
		dslc_hide_wysiwyg();

	}

/** 
 * Scroller ( For the modules and templatest listing )
 *
 * - dslc_scroller_init ( Initialize the scroller )
 * - dslc_scroller_go_to ( Scroll to specific item )
 * - dslc_scroller_prev ( Scroll to previous item )
 * - dslc_scroller_next ( Scroll to next item )
 */

	function dslc_scroller_init() {

		var scrollers = jQuery('.dslca-section-scroller');

		if ( scrollers.length ) {

			scrollers.each(function(){

				var scrollerItem;

				var scroller = jQuery(this);
				var scrollerInner = jQuery('.dslca-section-scroller-inner', scroller);
				var scrollerInnerOffset = scrollerInner.position();
				var scrollerContent = jQuery('.dslca-section-scroller-content', scroller);
				var scrollerItems = jQuery('.dslca-scroller-item:visible', scroller);

				var startingWidth = 0;
				var scrollerWidth = scroller.width() + scrollerInnerOffset.left * -1;
				var scrollerContentWidth = scrollerContent.width();

				jQuery('.dslca-section-scroller-item-last', scroller).removeClass('dslca-section-scroller-item-last');

				scrollerItems.each(function(){

					scrollerItem = jQuery(this);

					startingWidth += scrollerItem.outerWidth();

					if ( startingWidth > scrollerWidth ) {
						if ( jQuery('.dslca-section-scroller-item-last', scroller).length < 1 ) {
							scrollerItem.addClass('dslca-section-scroller-item-last');
							scroller.data( 'current', scrollerItem.prev('.dslca-scroller-item:visible').index() );
						}
					}

				});

			});

		}

	}

	function dslc_scroller_go_to( scrollerItemIndex, scroller ) {

		var scrollerInner = jQuery('.dslca-section-scroller-inner', scroller);
		var scrollerContent = jQuery('.dslca-section-scroller-content', scroller);
		var scrollerItems = jQuery('.dslca-scroller-item', scroller);
		var scrollerItem = jQuery('.dslca-scroller-item:eq(' + scrollerItemIndex + ')', scroller);

		if ( scrollerItem.length ) {

			var scrollerWidth = scroller.width();
			var scrollerContentWidth = scrollerContent.width();
			var scrollerItemOffset = scrollerItem.position();

			var scrollerNewOffset = ( scrollerWidth - ( scrollerItemOffset.left + scrollerItem.outerWidth() ) );

			if ( scrollerNewOffset < 0 ) {

				scroller.data( 'current', scrollerItemIndex );
				scrollerInner.stop().animate({ left : scrollerNewOffset }, 200);

			} else { 

				scrollerInner.stop().animate({ left : 0 }, 200);

			}

		}

	}

	function dslc_scroller_prev( scroller ) {

		var scrollerCurr = scroller.data('current');
		var scrollerNew = scroller.find('.dslca-scroller-item:eq(' + scrollerCurr +  ')').prev('.dslca-scroller-item:visible').index();

		if ( scrollerNew !== -1 )
			dslc_scroller_go_to( scrollerNew, scroller );

	}

	function dslc_scroller_next( scroller ) {

		var scrollerCurr = scroller.data('current');
		var scrollerNew = scroller.find('.dslca-scroller-item:eq(' + scrollerCurr +  ')').next('.dslca-scroller-item:visible').index();

		if ( scrollerNew !== -1 )
			dslc_scroller_go_to( scrollerNew, scroller );

	}

